<?phpclass HookInvoicecardFacturaeverifactu{    public function addMoreActionsButtons($parameters, &$object, &$action, $hookmanager)    {        global $langs;        // Confirmación visual        print '<div style="background:orange; padding:1em;">✅ Hook ejecutado correctamente en factura: ' . $object->ref . '</div>';        // O botón real        if ($object->element == 'facture' && $object->statut >= 1) {            $url = dol_buildpath('/custom/facturaeverifactu/action_generate.php', 1) . '?facid=' . $object->id;            print '<a class="butAction" href="' . $url . '">Generar Facturae</a>';        }        return 0;    }}